import React from "react"

function App() {

  const [data, setData] = React.useState(null)

  function getData(val) {
    setData(val.target.value)
    console.log(data)
  }

  const textStyle = {
    color: `${data}`
  }

  return (
    <div className="App">
      <h1 style={{textStyle}}>Hello!</h1>
      <input type="text" onKeyUp={getData} placeholder="Color"/>
    </div>
  );
}

export default App;
