import React from "react"

function App() {

  const [Color, setColor] = React.useState(null)

  function getColor(val) {
    setColor(val.target.value)
    console.log(Color)
  }

  return (
    <div className="App">
      <h1 style={{
        color: `${Color}`
      }}>
      Hello!
      </h1>
      <input type="text" onChange={getColor} placeholder="Color"/>
    </div>
  );
}

export default App;
