import React from "react"
import "./App.css"

function App() {

  const [color, setColor] = React.useState("#ffffff")
  const [fontSize, setFontSize] = React.useState(50)
  const [content, setContent] = React.useState("Button")
  const [fill, setFill] = React.useState("#000000")
  const [width, setWidth] = React.useState(480)
  const [height, setHeight] = React.useState(160)
  const [borderRadius, setBorderRadius] = React.useState(20)
  const [borderColor, setBorderColor] = React.useState("#D7D7D7")
  const [borderWidth, setBorderWidth] = React.useState(7)

  const [buttonData, setButtonData] = React.useState(
    {
      color: "#fff",
      fontSize: 50,
      content: "Button",
      fill: "#000",
      width: 480,
      height: 160,
      borderRadius: 20,
      borderColor: "#D7D7D7",
      borderWidth: 7
    }
  )

  function buttonHandler(input) {
    setButtonData(prevData => {
      return {
        ...prevData,
        [input.target.name]: input.target.value
      }
    })
  }

  function newColor(input) {
    setColor(input.target.value)
  }

  function newFontSize(input) {
    setFontSize(input.target.value)
  }

  function newContent(input) {
    setContent(input.target.value)
  }

  function newFill(input) {
    setFill(input.target.value)
  }

  function newWidth(input) {
    setWidth(input.target.value)
  }

  function newHeight(input) {
    setHeight(input.target.value)
  }

  function newBorderRadius(input) {
    setBorderRadius(input.target.value)
  }

  function newBorderColor(input) {
    setBorderColor(input.target.value)
  }

  function newBorderWidth(input) {
    setBorderWidth(input.target.value)
  }

  return (
    <div className="App">
      <div className="button" style={{
        color: `${buttonData.color}`,
        fontSize: `${buttonData.fontSize}px`,
        backgroundColor : `${buttonData.fill}`,
        width: `${buttonData.width}px`,
        height: `${buttonData.height}px`,
        border: `${buttonData.borderWidth}px solid ${buttonData.BorderColor}`,
        borderRadius: `${buttonData.newBorderRadius}px`
      }}>
        <p style={{textAlign: "center"}}>
          {buttonData.content}
        </p>
      </div>

      <div className="inputs">
        <input name="color" type="text" onChange={newColor} placeholder="Color"/>
        <input name="fontSize" type="text" onChange={newFontSize} placeholder="Font Size"/>
        <input name="content" type="text" onChange={newContent} placeholder="Content"/>
        <input name="fill" type="text" onChange={newFill} placeholder="Fill Color"/>
        <input name="width" type="text" onChange={newWidth} placeholder="Width"/>
        <input name="height" type="text" onChange={newHeight} placeholder="Height"/>
        <input name="borderRadius" type="text" onChange={newBorderRadius} placeholder="Border Radius"/>
        <input name="borderColor" type="text" onChange={newBorderColor} placeholder="Border Color"/>
        <input name="borderWidth" type="text" onChange={newBorderWidth} placeholder="Border Width"/>
      </div>

    </div>
  );
}

export default App;
