import React from "react"
import "./App.css"

function App() {


  const [buttonData, setButtonData] = React.useState(
    {
      color: "#fff",
      fontSize: 50,
      content: "Button",
      fill: "#000",
      width: 480,
      height: 160,
      borderRadius: 20,
      borderColor: "#D7D7D7",
      borderWidth: 7
    }
  )

  function buttonHandler(input) {
    setButtonData(prevData => {
      return {
        ...prevData,
        [input.target.name]: input.target.value
      }
    })
  }

  return (
    <div className="App">
      <div className="button" style={{
        color: `${buttonData.color}`,
        fontSize: `${buttonData.fontSize}px`,
        backgroundColor : `${buttonData.fill}`,
        width: `${buttonData.width}px`,
        height: `${buttonData.height}px`,
        border: `${buttonData.borderWidth}px solid ${buttonData.BorderColor}`,
        borderRadius: `${buttonData.newBorderRadius}px`
      }}>
        <p style={{textAlign: "center"}}>
          {buttonData.content}
        </p>
      </div>

      <div className="inputs">
        <input name="color" type="text" onChange={buttonHandler} placeholder="Color"/>
        <input name="fontSize" type="text" onChange={buttonHandler} placeholder="Font Size"/>
        <input name="content" type="text" onChange={buttonHandler} placeholder="Content"/>
        <input name="fill" type="text" onChange={buttonHandler} placeholder="Fill Color"/>
        <input name="width" type="text" onChange={buttonHandler} placeholder="Width"/>
        <input name="height" type="text" onChange={buttonHandler} placeholder="Height"/>
        <input name="borderRadius" type="text" onChange={buttonHandler} placeholder="Border Radius"/>
        <input name="borderColor" type="text" onChange={buttonHandler} placeholder="Border Color"/>
        <input name="borderWidth" type="text" onChange={buttonHandler} placeholder="Border Width"/>
      </div>

    </div>
  );
}

export default App;
