import React from "react"
import "./App.css"

function App() {

  const [color, setColor] = React.useState("#ffffff")
  const [fontSize, setFontSize] = React.useState(50)
  const [content, setContent] = React.useState("Button")
  const [fill, setFill] = React.useState("#000000")
  const [width, setWidth] = React.useState(480)
  const [height, setHeight] = React.useState(10)

  function newColor(input) {
    setColor(input.target.value)
  }

  function newFontSize(input) {
    setFontSize(input.target.value)
  }

  function newContent(input) {
    setContent(input.target.value)
  }

  function newFill(input) {
    setFill(input.target.value)
  }

  function newWidth(input) {
    setWidth(input.target.value)
  }

  function newHeight(input) {
    setHeight(input.target.value)
  }

  return (
    <div className="App">
      <div className="button" style={{
        color: `${color}`,
        fontSize: `${fontSize}px`,
        backgroundColor : `${fill}`,
        width: `${width}px`,
        height: `${height}px`
      }}>
        <p style={{textAlign: "center"}}>
          {content}
        </p>
      </div>

      <div className="inputs">
        <input type="text" onChange={newColor} placeholder="Color"/>
        <input type="text" onChange={newFontSize} placeholder="Font Size"/>
        <input type="text" onChange={newContent} placeholder="Content"/>
        <input type="text" onChange={newFill} placeholder="Fill Color"/>
        <input type="text" onChange={newWidth} placeholder="Width Color"/>
        <input type="text" onChange={newHeight} placeholder="Height Color"/>
      </div>

    </div>
  );
}

export default App;
