import { ToastContainer, toast, Slide, Zoom, Bounce, Flip } from 'react-toastify';
import { injectStyle } from "react-toastify/dist/inject-style";
import 'react-toastify/dist/ReactToastify.css';


if (typeof window !== "undefined") {
  injectStyle();
}

export default function Save() {
  const notify = () => toast.dark("Saved This Configuration!", {
    position: "top-right",
    autoClose: 5000,
    hideProgressBar: true,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    transition: Bounce
  });

  return (
    <div className='Save'>
      <button onClick={notify}>Save This</button>
      <ToastContainer style={
        {toastifyToastBackground: "red"}
      } />
    </div>

  )
}
